INSERT OR IGNORE INTO "Courses" ("id", "title", "description", "image", "resources", "creatorClerkId", "createdAt", "updatedAt") VALUES (46, 'Inducción SMS SAVIARE', 'Este curso introduce a los colaboradores al Sistema de Gestión de Seguridad Operacional (SMS) de SAVIARE. Aquí aprenderás los principios básicos del SMS, tus responsabilidades dentro del sistema y el uso de la plataforma para reportar, gestionar y dar seguimiento a eventos. Al finalizar, estarás preparado para participar activamente en la cultura de seguridad de la organización.', NULL, '[]', 'user_34TWX7M13fgqVw9cYoYgcfotRNP', '2025-11-18 15:59:16.398+00', '2025-11-18 15:59:16.398+00');
INSERT OR IGNORE INTO "Courses" ("id", "title", "description", "image", "resources", "creatorClerkId", "createdAt", "updatedAt") VALUES (47, 'Inducción Sistema de Gestión en Seguridad y Salud en el Trabajo', 'Este curso presenta los conceptos esenciales del Sistema de Gestión en Seguridad y Salud en el Trabajo (SG-SST). Conocerás tus responsabilidades, las prácticas básicas de prevención y los procedimientos que garantizan un entorno laboral seguro y saludable. Al finalizar, estarás preparado para participar activamente en el cumplimiento y mejora continua del sistema.', NULL, '[]', 'user_34TWX7M13fgqVw9cYoYgcfotRNP', '2025-11-18 16:28:11.603+00', '2025-11-18 16:28:11.603+00');
INSERT OR IGNORE INTO "Courses" ("id", "title", "description", "image", "resources", "creatorClerkId", "createdAt", "updatedAt") VALUES (48, 'Hábitos y Estilos de Vida Saludable', 'Este curso ofrece una introducción práctica a los hábitos y estilos de vida saludable que contribuyen al bienestar físico y mental. Aprenderás recomendaciones clave sobre alimentación, actividad física, manejo del estrés y autocuidado para mejorar tu calidad de vida dentro y fuera del trabajo.', NULL, '[]', 'user_34TWX7M13fgqVw9cYoYgcfotRNP', '2025-11-18 16:39:22.572+00', '2025-11-18 16:39:22.572+00');
INSERT OR IGNORE INTO "Courses" ("id", "title", "description", "image", "resources", "creatorClerkId", "createdAt", "updatedAt") VALUES (49, 'Uso de EPPs', 'Este curso brinda una guía clara y práctica sobre el uso correcto de los Equipos de Protección Personal (EPPs). Conocerás su importancia, los tipos de EPP según el riesgo, y las responsabilidades del trabajador para garantizar una protección adecuada en cada actividad. Al finalizar, tendrás las bases para seleccionar, utilizar y mantener correctamente tus elementos de protección.', NULL, '[]', 'user_34TWX7M13fgqVw9cYoYgcfotRNP', '2025-11-18 16:55:21.634+00', '2025-11-18 16:55:21.634+00');
